import logo from './assets/investment-calculator-logo.png';
import Form from './Components/NewInvestment/InvestmentForm';
import { useState } from 'react';
function App() {

  // const [investmentsData, setInvestmentsData] = useState('');
  const [yearlyData, setYearlyData] = useState([]);

  const calculateHandler = (userInput) => {
    // Should be triggered when form is submitted
    // You might not directly want to bind it to the submit event on the form though...

    // const userInputUpdated = { //add key on my new item
    //   id: Math.random().toString(),
    //   ...userInput
    // };
    // setInvestmentsData((prevExpenses) => {
    //   return [ userInputUpdated, ...prevExpenses];
    // });

    let currentSavings = +userInput.currentSavings; // + convert a variable to number if possible
    let yearlyContribution = +userInput.yearlySavings; // as mentioned: feel free to change the shape...
    const expectedReturn = +userInput.expectedInterest / 100;
    const duration = +userInput.investmentDuration;

    // The below code calculates yearly results (total savings, interest etc)
    for (let i = 0; i < duration; i++) {
      const yearlyInterest = currentSavings * expectedReturn;
      currentSavings += yearlyInterest + yearlyContribution;
      const data = { 
        id: Math.random().toString(),
        year: i + 1,
        yearlyInterest: yearlyInterest,
        savingsEndOfYear: currentSavings,
        yearlyContribution: yearlyContribution,
        totalYearlyContribution: yearlyContribution++,
        totalSavings: currentSavings++
      }
      setYearlyData(prevData => {
        return [data , ...prevData]
      });
    }
  };

  return (
    <div>
      <header className="header">
        <img src={logo} alt="logo" />
        <h1>Investment Calculator</h1>
      </header>

      <Form onSubmit={calculateHandler} />

      {/* Todo: Show below table conditionally (only once result data is available) */}
      {/* Show fallback text if no data is available */}

      <table className="result">
        <thead>
          <tr>
            <th>Year</th>
            <th>Total Savings</th>
            <th>Interest (Year)</th>
            <th>Total Interest</th>
            <th>Invested Capital</th>
          </tr>
        </thead>
        <tbody>

          {yearlyData.map((item,index) => (
            <tr key={index}>
              <td>{item.year}</td>
              <td>{item.savingsEndOfYear}</td>
              <td>{item.yearlyInterest}</td>
              <td>{item.totalYearlyContribution}</td>
              <td>{item.totalSavings}</td>
             </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
